test.config:
indexer
{
	mem_limit			= 16M
}

searchd
{
	
}

source src
{
	type			= mysql
	
	sql_query = SELECT document_id, text, mva, tag FROM test_table
	sql_attr_multi	= uint mva from field
	sql_attr_uint	= tag
}

index idx
{
	source			= src
	path			= /idx
	docinfo			= extern
	charset_type	= utf-8
	min_word_len	= 1
}

index dist
{
	type					= distributed
	agent					= :idx
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}
