test.config:
indexer
{
	mem_limit			= 16M
}

searchd
{

}

source src1
{
	type			= mysql


	sql_query		= SELECT id, group_id, title FROM test_table
	sql_attr_uint	= group_id
}

source src2 : src1
{
	sql_query		= SELECT 10+id, group_id, title FROM test_table
}


index block1
{
	source			= src1
	path			= /block1
	docinfo			= extern
	charset_type	= utf-8
	min_word_len	= 1
}



index block2
{
	source			= src2
	path			= /block2
	docinfo			= extern
	charset_type	= utf-8
	min_word_len	= 1
}



index dist
{
	type					= distributed
	local					= block1
	agent					= :block2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

test.custom_test:
$results = array ();

$res = $client->Query ( "", "dist" );
if ( !$res )
{
	$results[] = $client->GetLastError();
	return;
}
unset ( $res["time"] );
$results[] = $res;

$res = $client->UpdateAttributes ( "dist", array("group_id"), array(11=>array(123)) );
if ( $res===false )
{
	$results[] = $client->GetLastError();
	return;
}
$results[] = $res;

$res = $client->Query ( "", "dist" );
if ( !$res )
{
	$results[] = $client->GetLastError();
	return;
}
unset ( $res["time"] );
$results[] = $res;

test.db_create:
CREATE TABLE test_table
(
	id			INTEGER PRIMARY KEY NOT NULL AUTO_INCREMENT,
	group_id	INTEGER NOT NULL,
	title		VARCHAR(255) NOT NULL
);
