test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source srctest
{
	type = mysql

	sql_query = SELECT id, body FROM test_table
}

index test_idx
{
	source			= srctest
	path			= /test
	charset_type	= utf-8
	min_word_len	= 1
}

test.custom_test:
$results = array ();
$opts = array
(
	'before_match' => '[B]',
	'after_match' => '[A]',
	'chunk_separator' => ' ... ',
	'limit' => 100,
	'around' => 2,
);

$tests = array
(
	array
	(
		'docs' => array('He caught fish in deep pools with invisible fingers and ate them raw.'),
		'q' => array
		(
			'he* fin*',
			'*gers *raw',
			'fin* *gers',
			'*augh* *is*',
			'*pools*',
		),
	),

	// utf-8

	array
	(
		'docs' => array('Невидимыми пальцами ловил он рыбу в глубоких омутах и ел её сырой.'),
		'q' => array
		(
			'невидимыми* пальц*',
			'*цами *сырой',
			'пальц* *цами',
			'*ови* *ы*',
			'*омутах*',
		),
	),
);

foreach ( $tests as $test )
{
	foreach ( $test['q'] as $words )
	{
		$results [] = $words;
		$res = $client->BuildExcerpts ( $test['docs'], 'test_idx', $words, $opts );
		if ( !$res )
		{
			$results = false;
			return;
		}
		$results [] = $res;
	}
}
