test.custom_test:
$results = array ();

$results [] = $client->Open();

$results [] = $client->Query ( 'test' );
$results [] = $client->GetLastError();
sleep ( 2 );
$results [] = $client->Query ( 'test' );
$results [] = $client->GetLastError();

$results [] = $client->Close();

foreach ( $results as &$r )
	if ( is_array($r) )
		unset ( $r['time'] );
