test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source src_main
{
	type = mysql

	sql_query = SELECT * FROM test_table
}

source src_delta1
{
	type = mysql

	sql_query = SELECT * FROM test_table WHERE document_id=6
	sql_query_killlist = SELECT 1
}

source src_delta2
{
	type = mysql

	sql_query = SELECT * FROM test_table WHERE document_id=7
	sql_query_killlist = SELECT 2
}

source src_delta3
{
	type = mysql

	sql_query = SELECT * FROM test_table WHERE document_id=8
	sql_query_killlist = SELECT 3
}

index idx_main
{
	source				= src_main
	path				= /test_main
}

index idx_delta1
{
	source				= src_delta1
	path				= /test_delta1
}

index idx_delta2
{
	source				= src_delta2
	path				= /test_delta2
}

index idx_delta3
{
	source				= src_delta3
	path				= /test_delta3
}

test.db_create:
CREATE TABLE test_table
(
	`document_id` int(11) NOT NULL default '0',
	`body` varchar(255) NOT NULL default ''
);

test.custom_test:
$client->SetFilter ( "@id", array(1,2,3,4) );
$client->AddQuery ( "test", "idx_main idx_delta1 idx_delta2 idx_delta3" );
$client->ResetFilters ();
$client->SetFilter ( "@id", array(5,6,7,8) );
$client->AddQuery ( "test", "idx_main idx_delta1 idx_delta2 idx_delta3" );
$client->ResetFilters ();
$client->SetFilter ( "@id", array(1,2,3,4,5,6,7,8), "idx_main idx_delta1 idx_delta2 idx_delta3" );
$client->SetSortMode ( SPH_SORT_EXTENDED, "@id DESC" );
$client->AddQuery ( "test" );
$results = $client->RunQueries ();

if ( is_array($results) )
	for ( $i=0; $i<count($results); $i++ )
		if ( is_array($results[$i]) )
			unset ( $results[$i]["time"] );
