test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source src_a
{
	type = mysql


	sql_query = SELECT id, a, b, c, text FROM test_table WHERE idx = 'a';

	sql_attr_uint = a
	sql_attr_uint = b
	sql_attr_uint = c:1
}

source src_delta
{
	type = mysql


	sql_query = SELECT id, b, a, c, text FROM test_table WHERE idx = 'delta';

	sql_attr_uint = a
	sql_attr_uint = b
	sql_attr_uint = c:1
}

source src_a2
{
	type = mysql


	sql_query = SELECT id, a, b, c, text FROM test_table WHERE idx = 'a';

	sql_attr_uint = a
	sql_attr_uint = b
	sql_attr_uint = c:2
}

source src_b
{
	type = mysql


	sql_query = SELECT id, a, b, text FROM test_table WHERE idx = 'b';

	sql_attr_uint = a
	sql_attr_uint = b
}

source src_c
{
	type = mysql


	sql_query = SELECT id, mva as a, text FROM test_table WHERE idx = 'c';
	sql_attr_multi = uint a from field
}

index a
{
	source	= src_a
	path	= /a
}

index delta
{
	source	= src_delta
	path	= /delta
}

index a2
{
	source	= src_a2
	path	= /a2
}

index b
{
	source	= src_b
	path	= /b
}

index c
{
	source	= src_c
	path	= /c
}
